mapper.select:
SELECT masterID

FROM TB_Decision 

where seqNo = #{decisionId}

SELECT *

FROM TB_Decision_Opinion_Item 


	reptSeq = #{reptSeq}
	AND reptOwnrSeq = #{reptOwnrSeq} 
	AND opinionType = #{opinionType}
	AND delCheck = '0'

SELECT *

FROM TB_Decision_File_Opinion 


	reptSeq = #{reptOwnrSeq}
	AND reptOwnrSeq = #{reptSeq}
	AND delCheck = '0'

SELECT DI.seqNo ,DI.decisionId ,DI.reptSeq ,DI.opinionType ,DI.opinionTitle ,DI.opinionContent ,DI.opinionItemFile ,DI.rank ,DI.delCheck ,DI.regdate ,TF.fileNameChange ,TF.fileFolder FROM TB_Decision_Opinion_Item DI LEFT JOIN TB_Decision_File_Opinion DFO ON DI.decisionId = DFO.decisonId AND DI.opinionType = DFO.fileType LEFT JOIN TB_File TF ON DFO.fileSeq = TF.seqNo
DI.reptSeq = #{viewSeq}
AND DI.opinionType = #{type} AND DI.delCheck = '0'

SELECT count(*) as listCnt

FROM TB_Decision_Opinion_Item 


	reptSeq = #{viewSeq}
	AND opinionType = #{type}
	AND delCheck = '0'

        SELECT * 
        FROM TB_FILE 

			seqNo = #{seqNo}
