mapper.select:
        SELECT COUNT(*)
		FROM TB_Law 
		WHERE del_check = 0


	         AND title = #{title}




	         AND article = #{article}




	         AND paragraph = #{paragraph}

        SELECT *
		FROM TB_Law
		WHERE del_check = 0


	         AND title = #{title}




	         AND article = #{article}




	         AND paragraph = #{paragraph}



		ORDER BY seq_no DESC

		OFFSET (#{page}-1)* #{listSize} ROW
		FETCH NEXT 10 ROW ONLY

        SELECT paragraph
		FROM TB_Law
		WHERE del_check = 0
		AND title = #{title}
		AND article = #{article}
		GROUP BY paragraph
